$(document).ready(function () {
  $(document).on("click", ".slicknav_btn", function () {
    if ($(".slicknav_nav").hasClass("slicknav_hidden")) {
      $(".slicknav_nav")
        .fadeIn()
        .removeClass("slicknav_hidden")
        .addClass("slicknav_active");
    } else {
      $(".slicknav_nav")
        .fadeOut()
        .removeClass("slicknav_active")
        .addClass("slicknav_hidden");
    }
  });
  $(document).on("click", ".slicknav_arrow", function () {
    if ($(".dropdown").hasClass("slicknav_hidden")) {
      $(".dropdown")
        .fadeIn()
        .removeClass("slicknav_hidden")
        .addClass("slicknav_active");
      $(".slicknav_arrow").html("▼");
    } else {
      $(".dropdown")
        .fadeOut()
        .removeClass("slicknav_active")
        .addClass("slicknav_hidden");
      $(".slicknav_arrow").html("►");
    }
  });
});

/* SET COOKIE */
function darkMode() {
  $("html,body").removeAttr("class");
  $("html,body").removeAttr("style");
  $("html,body").addClass("dark-mode-active");
  $("html,body").css("background", "rgb(36, 41, 51)");
}
$(".enable_dark_mode").on("click", function () {
  if ($(this).is(":checked")) {
    $.cookie("EnableDark", 1, { expires: 1, path: "/" });
    $.cookie("EnableDark", 1, { expires: 1, path: "/read/" });
    $.cookie("EnableDark", 1, { expires: 1, path: "/genre/" });
    $.cookie("EnableDark", 1, { expires: 1, path: "/search" });
    darkMode();
    $(".enable_dark_mode").prop("checked", true);
  } else {
    $.cookie("EnableDark", 0, { expires: 1, path: "/" });
    $.cookie("EnableDark", 0, { expires: 1, path: "/read/" });
    $.cookie("EnableDark", 0, { expires: 1, path: "/genre/" });
    $.cookie("EnableDark", 0, { expires: 1, path: "/search" });
    $("html,body").removeAttr("class");
    $("html,body").removeAttr("style");
    $(".enable_dark_mode").prop("checked", false);
  }
});

/* GET COOKIE */
if ($.cookie("EnableDark") == 1) {
  darkMode();
  $(".enable_dark_mode").prop("checked", true);
} else {
  $("html,body").removeAttr("class");
  $("html,body").removeAttr("style");
  $(".enable_dark_mode").prop("checked", false);
}

$(document).ready(function () {
  $(".btn-synopsis").on("click", function () {
    if ($(".hide-synopsis").hasClass("active")) {
      $(".hide-synopsis")
        .removeClass("active")
        .removeAttr("style")
        .css("height", "120px");
      $(".btn-synopsis").html("<i class='fa fa-eye-slash'></i> Show Less");
    } else {
      $(".hide-synopsis")
        .addClass("active")
        .removeAttr("style")
        .css("height", "unset");
      $(".btn-synopsis").html("<i class='fa fa-eye'></i> Read More");
    }
  });
  var x = $(".anime__tools").html();
  $(".anime__tools__object").html(x);
});
